import{_ as o}from"./plugin-vue_export-helper.21dcd24c.js";import{o as p,c as s,a,b as e,e as t,d as n,r as i}from"./app.6a2d513d.js";const c={},h=a("h1",{id:"rabbitmq\u9762\u8BD5\u9898",tabindex:"-1"},[a("a",{class:"header-anchor",href:"#rabbitmq\u9762\u8BD5\u9898","aria-hidden":"true"},"#"),t(" RabbitMQ\u9762\u8BD5\u9898")],-1),g=a("h2",{id:"_0-\u53C2\u8003",tabindex:"-1"},[a("a",{class:"header-anchor",href:"#_0-\u53C2\u8003","aria-hidden":"true"},"#"),t(" 0.\u53C2\u8003")],-1),d={href:"https://doocs.gitee.io/advanced-java/#/",target:"_blank",rel:"noopener noreferrer"},l=t("https://doocs.gitee.io/advanced-java/#/"),u=n('<h2 id="_1-\u4E3A\u4EC0\u4E48\u4F7F\u7528-mq-\u597D\u5904" tabindex="-1"><a class="header-anchor" href="#_1-\u4E3A\u4EC0\u4E48\u4F7F\u7528-mq-\u597D\u5904" aria-hidden="true">#</a> 1.\u4E3A\u4EC0\u4E48\u4F7F\u7528 MQ\uFF08\u597D\u5904\uFF09</h2><p>\u4F18\u70B9\uFF1A<strong>\u89E3\u8026\u3001\u5F02\u6B65\u3001\u524A\u5CF0</strong></p><p>1.<strong>\u89E3\u8026</strong>\uFF1A\u7CFB\u7EDFA\u9700\u8981\u53D1\u9001\u6D88\u606F\u5230\u7CFB\u7EDFB\u3001C\u3001D\uFF0C\u82E5\u6B64\u65F6\u7CFB\u7EDFC\u4E0D\u9700\u8981\u4E86\u5462\uFF1F\u7CFB\u7EDFE\u8981\u52A0\u5165\u8FDB\u6765\u5462\uFF1F\u8FD9\u6837\u7684\u8BDD\u7CFB\u7EDF\u8026\u5408\u5EA6\u9AD8\u3002\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528 MQ\uFF0C\u7CFB\u7EDFA\u53D1\u6D88\u606F\u5230 MQ \u4E2D\uFF0C\u7CFB\u7EDFB\u3001C\u3001D\u3001E\u8C01\u9700\u8981\u6D88\u606F\u7684\u8BDD\u5C31\u53BB MQ \u4E2D\u6D88\u8D39\uFF0C\u8FD9\u6837\u5C31\u5B9E\u73B0\u4E86\u7CFB\u7EDF\u95F4\u7684\u89E3\u8026\u3002</p><p><strong>\u9762\u8BD5\u6280\u5DE7</strong>\uFF1A\u4F60\u9700\u8981\u53BB\u8003\u8651\u4E00\u4E0B\u4F60\u8D1F\u8D23\u7684\u7CFB\u7EDF\u4E2D\u662F\u5426\u6709\u7C7B\u4F3C\u7684\u573A\u666F\uFF0C\u5C31\u662F\u4E00\u4E2A\u7CFB\u7EDF\u6216\u8005\u4E00\u4E2A\u6A21\u5757\uFF0C\u8C03\u7528\u4E86\u591A\u4E2A\u7CFB\u7EDF\u6216\u8005\u6A21\u5757\uFF0C\u4E92\u76F8\u4E4B\u95F4\u7684\u8C03\u7528\u5F88\u590D\u6742\uFF0C\u7EF4\u62A4\u8D77\u6765\u5F88\u9EBB\u70E6\u3002\u4F46\u662F\u5176\u5B9E\u8FD9\u4E2A\u8C03\u7528\u662F\u4E0D\u9700\u8981\u76F4\u63A5\u540C\u6B65\u8C03\u7528\u63A5\u53E3\u7684\uFF0C\u5982\u679C\u7528 MQ \u7ED9\u5B83\u5F02\u6B65\u5316\u89E3\u8026\uFF0C\u4E5F\u662F\u53EF\u4EE5\u7684\uFF0C\u4F60\u5C31\u9700\u8981\u53BB\u8003\u8651\u5728\u4F60\u7684\u9879\u76EE\u91CC\uFF0C\u662F\u4E0D\u662F\u53EF\u4EE5\u8FD0\u7528\u8FD9\u4E2A MQ \u53BB\u8FDB\u884C\u7CFB\u7EDF\u7684\u89E3\u8026\u3002\u5728\u7B80\u5386\u4E2D\u4F53\u73B0\u51FA\u6765\u8FD9\u5757\u4E1C\u897F\uFF0C\u7528 MQ \u4F5C\u89E3\u8026\u3002</p><p>2.<strong>\u5F02\u6B65</strong>\uFF1A\u7CFB\u7EDFA\u8C03\u7528\u7CFB\u7EDFB\u4E2D\u7684\u65B9\u6CD5\uFF0CB\u7684\u6267\u884C\u65F6\u95F4\u5F88\u957F\uFF0C\u4F46\u662FA\u53C8\u9700\u8981\u77E5\u9053B\u4EC0\u4E48\u65F6\u5019\u6267\u884C\u5B8C\uFF0C\u6CA1\u6709 MQ \u4E4B\u524D\u7684\u505A\u6CD5\u662FA\u6BCF\u9694\u4E00\u6BB5\u65F6\u95F4\u53BB\u8C03\u7528B\u7684\u67E5\u8BE2 API\uFF0C\u6216\u8005\u662FA\u63D0\u4F9B\u4E00\u4E2A\u56DE\u8C03\u63A5\u53E3\uFF0C\u8FD9\u4E24\u79CD\u65B9\u6CD5\u90FD\u4E0D\u662F\u5F88\u4F18\u96C5\uFF0C\u6211\u4EEC\u5F15\u5165 MQ \u4E4B\u540E\uFF0CB\u6267\u884C\u5B8C\u6210\u540E\u53D1\u9001\u6D88\u606F\u5230 MQ \u4E2D\uFF0C\u7136\u540E\u7531 MQ \u53BB\u901A\u77E5A\u65B9\u6CD5\u5DF2\u7ECF\u6267\u884C\u5B8C\u6210\u3002</p><p>3.<strong>\u524A\u5CF0</strong>\uFF1A\u51CF\u5C11\u9AD8\u5CF0\u65F6\u671F\u5BF9\u670D\u52A1\u5668\u7684\u538B\u529B\u3002</p><p>\u7F3A\u70B9\uFF1A</p><p>1.<strong>\u7CFB\u7EDF\u53EF\u7528\u6027\u964D\u4F4E</strong>\uFF1AMQ \u5B95\u673A</p><p>2.<strong>\u7CFB\u7EDF\u590D\u6742\u5EA6\u63D0\u9AD8</strong>\uFF1A\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u4E0D\u4E22\u5931\uFF1F\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u4E0D\u88AB\u91CD\u590D\u6D88\u8D39\uFF1F\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u7684\u987A\u5E8F\u6027\uFF1F</p><p>3.<strong>\u4E00\u81F4\u6027\u95EE\u9898</strong>\uFF1AA\u7CFB\u7EDF\u6267\u884C\u6210\u529F\u5C31\u8FD4\u56DE\u4E86\uFF0C\u4F46\u662FB\u7CFB\u7EDF\u6267\u884C\u5931\u8D25\u4E86\u600E\u4E48\u529E\uFF1F</p><h2 id="_2-kafka\u3001activemq\u3001rabbitmq\u3001rocketmq-\u6709\u4EC0\u4E48\u4F18\u7F3A\u70B9" tabindex="-1"><a class="header-anchor" href="#_2-kafka\u3001activemq\u3001rabbitmq\u3001rocketmq-\u6709\u4EC0\u4E48\u4F18\u7F3A\u70B9" aria-hidden="true">#</a> 2.Kafka\u3001ActiveMQ\u3001RabbitMQ\u3001RocketMQ \u6709\u4EC0\u4E48\u4F18\u7F3A\u70B9\uFF1F</h2><p>Kafka\uFF1A\u7528\u4E8E <strong>\u5927\u6570\u636E</strong>\uFF0C\u6709\u65E5\u5FD7\u65B9\u9762\u7684\u9700\u6C42\u3002</p><p>ActiveMQ\uFF1A\u4E0D\u5E38\u7528\uFF0C\u793E\u533A\u6D3B\u8DC3\u5EA6\u4F4E\u3002</p><p>RabbitMQ\uFF1A<strong>\u65F6\u6548\u6027\u5FAE\u79D2\u7EA7</strong>\uFF0C\u6BD4\u8F83\u5E38\u7528\u3002</p><p>RockerMQ\uFF1A\u7ECF\u5386\u8FC7\u53CC\u5341\u4E00\u7B49\u6D3B\u52A8\u7684\u8003\u9A8C\uFF0C\u9002\u7528\u4E8E\u6709 <strong>\u5E76\u53D1</strong> \u573A\u666F\u7684\u9700\u6C42\u3002</p><h2 id="_3-\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u7684\u9AD8\u53EF\u7528" tabindex="-1"><a class="header-anchor" href="#_3-\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u7684\u9AD8\u53EF\u7528" aria-hidden="true">#</a> 3.\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u7684\u9AD8\u53EF\u7528\uFF1F</h2><p>RabbitMQ\u6709\u4E09\u79CD\u6A21\u5F0F\uFF1A\u5355\u673A\u6A21\u5F0F\u3001\u666E\u901A\u96C6\u7FA4\u6A21\u5F0F\u3001\u955C\u50CF\u96C6\u7FA4\u6A21\u5F0F</p><p>1.\u5355\u673A\u6A21\u5F0F</p><p>\u672C\u5730\u73AF\u5883\u4E0B\u6D4B\u8BD5\u7740\u73A9\u7684\u3002</p><p>2.\u666E\u901A\u96C6\u7FA4\u6A21\u5F0F</p><p>\u542F\u52A8\u591A\u4E2A RabbitMQ \u5B9E\u4F8B\uFF0C\u521B\u5EFA\u961F\u5217\u65F6\u53EA\u4F1A\u5728\u4E00\u53F0\u673A\u5668\u4E2D\uFF0C\u5176\u4ED6\u961F\u5217\u4FDD\u5B58\u7740\u8FD9\u4E2A\u961F\u5217\u7684\u5143\u6570\u636E\uFF08\u53EF\u4EE5\u627E\u5230\u961F\u5217\u6240\u5728\u7684\u5B9E\u4F8B\uFF09\uFF0C\u5F53\u4F60\u5728\u5176\u4ED6\u673A\u5668\u8BBF\u95EE\u8FD9\u4E2A\u961F\u5217\u65F6\uFF0C\u4F1A\u4ECE\u62E5\u6709\u8FD9\u4E2A\u961F\u5217\u7684\u673A\u5668\u4E2D\u62C9\u53D6\u961F\u5217\u3002</p><p><strong>\u6CA1\u6709\u505A\u5230\u771F\u6B63\u7684\u9AD8\u53EF\u7528</strong>\uFF0C\u62E5\u6709\u8FD9\u4E2A\u961F\u5217\u7684\u673A\u5668\u5B95\u673A\u65F6\uFF0C\u4E5F\u5C31\u65E0\u6CD5\u901A\u8FC7\u522B\u7684\u673A\u5668\u8BBF\u95EE\u5230\u961F\u5217\u4E86\u3002\u6B64\u5916\uFF0C\u8FD8\u6709\u6570\u636E\u62C9\u53D6\u7684\u5F00\u9500\u3002</p><p>\u4E3B\u8981\u662F <strong>\u63D0\u9AD8\u541E\u5410\u91CF</strong>\uFF0C\u8BA9\u591A\u4E2A\u8282\u70B9\u670D\u52A1\u67D0\u4E2A\u961F\u5217\u3002</p><p><img src="https://xingqiu-tuchuang-1256524210.cos.ap-shanghai.myqcloud.com/2541/202209040905938.png" alt="mq-7"></p><p>3.\u955C\u50CF\u96C6\u7FA4\u6A21\u5F0F</p><p>\u9AD8\u53EF\u7528\uFF0C\u961F\u5217\u7684\u5143\u6570\u636E\u548C\u6D88\u606F\u90FD\u4F1A\u5B58\u5728\u4E8E\u591A\u4E2A\u5B9E\u4F8B\u4E0A\uFF0C\u6BCF\u6B21\u5199\u961F\u5217\u7684\u65F6\u5019\uFF0C\u4F1A\u81EA\u52A8\u628A\u6D88\u606F\u540C\u6B65\u7ED9\u5176\u4ED6\u5B9E\u4F8B\u3002</p><p><strong>\u5982\u4F55\u5F00\u542F</strong>\uFF1F</p><p>\u5728\u540E\u9762\u6DFB\u52A0\u955C\u50CF\u96C6\u7FA4\u7684\u7B56\u7565\uFF0C\u53EF\u4EE5\u9009\u62E9\u5C06\u6D88\u606F\u540C\u6B65\u7ED9\u6240\u6709\u5B9E\u4F8B\uFF0C\u4E5F\u53EF\u4EE5\u9009\u62E9\u6307\u5B9A\u7684\u5B9E\u4F8B\u3002</p><p><img src="https://xingqiu-tuchuang-1256524210.cos.ap-shanghai.myqcloud.com/2541/202209040909806.png" alt="mq-8"></p><p><strong>\u7F3A\u70B9</strong>\uFF1A</p><p>1.\u6027\u80FD\u5F00\u9500\u5927\uFF0C\u6D88\u606F\u9700\u8981\u540C\u6B65\u5230\u6240\u6709\u7684\u5B9E\u4F8B\u4E0A\u3002</p><p>2.\u6CA1\u6709\u505A\u5230\u5206\u5E03\u5F0F\uFF0C\u5047\u8BBE\u4F60\u7684\u961F\u5217\u8D1F\u8F7D\u5F88\u91CD\uFF0C\u4F60\u65B0\u589E\u5B9E\u4F8B\uFF0C\u8FD9\u4E2A\u5B9E\u4F8B\u4E5F\u5305\u542B\u4E86\u8FD9\u4E2A\u961F\u5217\u7684\u6240\u6709\u6570\u636E\uFF0C\u6CA1\u6709\u6269\u5C55\u6027\u3002</p><h2 id="_4-\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u4E0D\u88AB\u91CD\u590D\u6D88\u8D39-\u6216\u8005\u8BF4-\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u6D88\u8D39\u7684\u5E42\u7B49\u6027" tabindex="-1"><a class="header-anchor" href="#_4-\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u4E0D\u88AB\u91CD\u590D\u6D88\u8D39-\u6216\u8005\u8BF4-\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u6D88\u8D39\u7684\u5E42\u7B49\u6027" aria-hidden="true">#</a> 4.\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u4E0D\u88AB\u91CD\u590D\u6D88\u8D39\uFF1F\u6216\u8005\u8BF4\uFF0C\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u6D88\u8D39\u7684\u5E42\u7B49\u6027\uFF1F</h2><p>\u5F53\u6D88\u8D39\u8005\u6D88\u8D39\u5B8C\u6D88\u606F\uFF0C\u4F46\u8FD8\u6CA1 ack \u65F6\u5B95\u673A\u4E86\uFF0CMQ \u4E0D\u77E5\u9053\u8FD9\u6761\u6D88\u606F\u5DF2\u7ECF\u6D88\u8D39\uFF0C\u53EF\u80FD\u4F1A\u91CD\u8BD5\uFF0C\u5C31\u6709\u53EF\u80FD\u9020\u6210\u91CD\u590D\u6D88\u8D39\u95EE\u9898\u3002</p><p>\u91CD\u590D\u6D88\u606F\u4E0D\u53EF\u6015\uFF0C\u91CD\u8981\u7684\u662F\u8981\u4FDD\u8BC1 \u6D88\u606F\u6D88\u8D39\u7684\u5E42\u7B49\u6027\u3002</p><p>\u5E42\u7B49\u6027\uFF1A<strong>\u591A\u4E2A\u76F8\u540C\u7684\u8BF7\u6C42\uFF0C\u9700\u8981\u4FDD\u8BC1\u5BF9\u5E94\u6570\u636E\u4E0D\u4F1A\u53D8\uFF0C\u4E0D\u80FD\u51FA\u9519\u3002</strong></p><p>\u601D\u8DEF\uFF1A</p><p>1.\u63D2\u5165\u4E4B\u524D\u5224\u65AD\u6570\u636E\u662F\u5426\u5DF2\u7ECF\u5B58\u5728\uFF0C\u5B58\u5728\u5C31\u8BDD\u5C31\u66F4\u65B0\u3002</p><p>2.\u6570\u636E\u5E93\u6DFB\u52A0\u552F\u4E00\u7EA6\u675F\uFF0C\u4FDD\u8BC1\u591A\u4E2A\u91CD\u590D\u6570\u636E\u53EA\u80FD\u63D2\u5165\u4E00\u6B21\u3002</p><p>3.\u5982\u679C\u662F\u5199\u5165 Redis\uFF0C\u90A3\u4E48 set \u547D\u4EE4\u5929\u7136\u5177\u6709\u5E42\u7B49\u6027\u3002</p><p>4.\u6BCF\u6B21\u53D1\u6D88\u606F\u65F6\uFF0C\u751F\u6210\u4E00\u4E2A\u5168\u5C40\u552F\u4E00ID\uFF0C\u6D88\u8D39\u8005\u6D88\u8D39\u4E4B\u524D\uFF0C\u6839\u636E ID \u67E5\u8BE2 Redis, \u5982\u679C\u4E0D\u5B58\u5728\uFF0C\u8BF4\u660E\u8FD8\u6CA1\u6709\u88AB\u6D88\u8D39\uFF0C\u6B64\u65F6\u53EF\u4EE5\u6D88\u8D39\u6D88\u606F\uFF0C\u4E4B\u540E\u628A ID \u5199\u5165 Redis \u4E2D\uFF1B\u5DF2\u7ECF\u5B58\u5728\u7684\u8BDD\u5C31\u4E0D\u5904\u7406\u3002</p><h2 id="_5-\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u7684\u53EF\u9760\u6027\u4F20\u8F93-\u6216\u8005\u8BF4-\u5982\u4F55\u5904\u7406\u6D88\u606F\u4E22\u5931\u7684\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#_5-\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u7684\u53EF\u9760\u6027\u4F20\u8F93-\u6216\u8005\u8BF4-\u5982\u4F55\u5904\u7406\u6D88\u606F\u4E22\u5931\u7684\u95EE\u9898" aria-hidden="true">#</a> 5.\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u7684\u53EF\u9760\u6027\u4F20\u8F93\uFF1F\u6216\u8005\u8BF4\uFF0C\u5982\u4F55\u5904\u7406\u6D88\u606F\u4E22\u5931\u7684\u95EE\u9898\uFF1F</h2><p>\u6D88\u606F\u4E22\u5931\u53EF\u80FD\u51FA\u73B0\u5728 <strong>\u751F\u4EA7\u8005\u3001MQ\u3001\u6D88\u8D39\u8005\u7AEF</strong>\u3002</p><p>1.\u751F\u4EA7\u8005\u7AEF\u4E22\u5931\u6D88\u606F</p><p>\u5F00\u542F <strong>\u53D1\u5E03\u786E\u8BA4</strong> \u6A21\u5F0F\uFF0CRabbitMQ \u5904\u7406\u5B8C\u6D88\u606F\uFF0C\u4F1A\u8C03\u7528\u751F\u4EA7\u8005\u7684 ack \u56DE\u8C03\u63A5\u53E3\uFF0CRabbitMQ \u6CA1\u80FD\u5904\u7406\u6D88\u606F\uFF0C\u4F1A\u8C03\u7528\u751F\u4EA7\u8005\u7684 nack \u56DE\u8C03\u63A5\u53E3\u3002</p><p>\u53D1\u5E03\u786E\u8BA4\u6709\u4E09\u79CD\u6A21\u5F0F\uFF1A</p><ul><li>\u5355\u4E2A\u786E\u8BA4</li><li>\u6279\u91CF\u786E\u8BA4</li><li>\u5F02\u6B65\u786E\u8BA4</li></ul>',47),m={href:"http://2.MQ",target:"_blank",rel:"noopener noreferrer"},_=t("2.MQ"),b=t(" \u4E22\u5931\u6D88\u606F"),q=n('<p>\u5F00\u542F RabbitMQ \u6301\u4E45\u5316</p><ul><li>\u521B\u5EFA\u961F\u5217\u65F6\u58F0\u660E\u6301\u4E45\u5316</li><li>\u53D1\u9001\u6D88\u606F\u65F6\u58F0\u660E\u6301\u4E45\u5316</li></ul><p>\u5F00\u542F\u6301\u4E45\u5316\u4E5F\u6709\u53EF\u80FD\u4E22\u5931\u6D88\u606F\uFF0C\u6BD4\u5982 RabbitMQ \u6D88\u8D39\u5B8C\u6D88\u606F\u540E\uFF0C\u51C6\u5907\u5199\u5165\u78C1\u76D8\uFF0C\u4F46\u8FD9\u65F6\u5019\u5B95\u673A\u4E86\uFF0C\u5BFC\u81F4\u5C11\u91CF\u6570\u636E\u4E22\u5931\u3002</p><p>\u6211\u4EEC\u53EF\u4EE5\u642D\u914D\u53D1\u5E03\u786E\u8BA4\u6A21\u5F0F\uFF0C\u5728\u5199\u5165\u78C1\u76D8\u540E\u624D ack\u3002</p><p>3.\u6D88\u8D39\u8005\u4E22\u5931\u6D88\u606F</p><p>\u4F7F\u7528 \u6D88\u606F\u5E94\u7B54\u3002\u6D88\u8D39\u8005\u6D88\u8D39\u6D88\u606F\u540E\uFF0C\u624B\u52A8 ack\u3002</p><p><img src="https://xingqiu-tuchuang-1256524210.cos.ap-shanghai.myqcloud.com/2541/202209040933388.png" alt="rabbitmq-message-lose-solution"></p><h2 id="_6-\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u7684\u987A\u5E8F\u6027" tabindex="-1"><a class="header-anchor" href="#_6-\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u7684\u987A\u5E8F\u6027" aria-hidden="true">#</a> 6.\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u7684\u987A\u5E8F\u6027\uFF1F</h2><p>\u5148\u770B\u987A\u5E8F\u4F1A\u9519\u8BEF\u7684\u573A\u666F binlog \u540C\u6B65\uFF1A</p><p>\u6267\u884C\u589E\u6539\u5220\uFF0C\u751F\u6210 3 \u6761 binlog \u8BED\u53E5\uFF0C\u53D1\u9001\u5230\u961F\u5217\u4E2D\uFF0C\u4E00\u4E2A\u961F\u5217\u5BF9\u5E94\u591A\u4E2A\u6D88\u8D39\u8005\uFF0C\u82E5\u6D88\u8D39\u80052\u6267\u884C\u8F83\u5FEB\uFF0C\u4E5F\u5C31\u662F\u4FEE\u6539\u64CD\u4F5C\u5148\u5B8C\u6210\u4E86\uFF0C\u8FD9\u6837\u7684\u8BDD\u987A\u5E8F\u5C31\u4E71\u4E86\u3002</p><p><img src="https://xingqiu-tuchuang-1256524210.cos.ap-shanghai.myqcloud.com/2541/202209040946038.png" alt="rabbitmq-order-01"></p><p>\u89E3\u51B3\uFF1A</p><p>1.\u62C6\u5206\u6210\u591A\u4E2A queue, \u4E00\u4E2A queue \u5BF9\u5E94\u4E00\u4E2A\u6D88\u8D39\u8005</p><p><img src="https://xingqiu-tuchuang-1256524210.cos.ap-shanghai.myqcloud.com/2541/202209040946371.png" alt="\u5728\u8FD9\u91CC\u63D2\u5165\u56FE\u7247\u63CF\u8FF0"></p><p>2.\u4E00\u4E2A queue \u5BF9\u5E94\u4E00\u4E2A\u6D88\u8D39\u8005\uFF0C\u6D88\u8D39\u8005\u5185\u90E8\u4F7F\u7528 <strong>\u5185\u5B58\u961F\u5217</strong> \u505A\u6392\u961F\uFF0C\u7136\u540E\u5206\u53D1\u7ED9\u4E0D\u540C\u7684 worker \u5904\u7406\u3002</p><p><img src="https://xingqiu-tuchuang-1256524210.cos.ap-shanghai.myqcloud.com/2541/202209040949090.png" alt="\u5728\u8FD9\u91CC\u63D2\u5165\u56FE\u7247\u63CF\u8FF0"></p><h2 id="_7-\u5982\u4F55\u8BBE\u8BA1\u4E00\u4E2A\u6D88\u606F\u961F\u5217" tabindex="-1"><a class="header-anchor" href="#_7-\u5982\u4F55\u8BBE\u8BA1\u4E00\u4E2A\u6D88\u606F\u961F\u5217" aria-hidden="true">#</a> 7.\u5982\u4F55\u8BBE\u8BA1\u4E00\u4E2A\u6D88\u606F\u961F\u5217\uFF1F</h2><ul><li>\u9996\u5148\u8FD9\u4E2A mq \u5F97 <strong>\u652F\u6301\u53EF\u4F38\u7F29\u6027</strong> \u5427\uFF0C\u5C31\u662F\u9700\u8981\u7684\u65F6\u5019 <strong>\u5FEB\u901F\u6269\u5BB9</strong>\uFF0C\u5C31\u53EF\u4EE5\u589E\u52A0\u541E\u5410\u91CF\u548C\u5BB9\u91CF\uFF0C\u90A3\u600E\u4E48\u641E\uFF1F\u8BBE\u8BA1\u4E2A\u5206\u5E03\u5F0F\u7684\u7CFB\u7EDF\u5457\uFF0C\u53C2\u7167\u4E00\u4E0B kafka \u7684\u8BBE\u8BA1\u7406\u5FF5\uFF0Cbroker -&gt; topic -&gt; partition\uFF0C\u6BCF\u4E2A partition \u653E\u4E00\u4E2A\u673A\u5668\uFF0C\u5C31\u5B58\u4E00\u90E8\u5206\u6570\u636E\u3002\u5982\u679C\u73B0\u5728\u8D44\u6E90\u4E0D\u591F\u4E86\uFF0C\u7B80\u5355\u554A\uFF0C\u7ED9 topic \u589E\u52A0 partition\uFF0C\u7136\u540E\u505A\u6570\u636E\u8FC1\u79FB\uFF0C\u589E\u52A0\u673A\u5668\uFF0C\u4E0D\u5C31\u53EF\u4EE5\u5B58\u653E\u66F4\u591A\u6570\u636E\uFF0C\u63D0\u4F9B\u66F4\u9AD8\u7684\u541E\u5410\u91CF\u4E86\uFF1F</li><li>\u5176\u6B21\u4F60\u5F97\u8003\u8651\u4E00\u4E0B <strong>\u8FD9\u4E2A mq \u7684\u6570\u636E\u8981\u4E0D\u8981\u843D\u5730\u78C1\u76D8</strong> \u5427\uFF1F\u90A3\u80AF\u5B9A\u8981\u4E86\uFF0C\u843D\u78C1\u76D8\u624D\u80FD\u4FDD\u8BC1\u522B\u8FDB\u7A0B\u6302\u4E86\u6570\u636E\u5C31\u4E22\u4E86\u3002\u90A3\u843D\u78C1\u76D8\u7684\u65F6\u5019\u600E\u4E48\u843D\u554A\uFF1F\u987A\u5E8F\u5199\uFF0C\u8FD9\u6837\u5C31\u6CA1\u6709\u78C1\u76D8\u968F\u673A\u8BFB\u5199\u7684\u5BFB\u5740\u5F00\u9500\uFF0C\u78C1\u76D8\u987A\u5E8F\u8BFB\u5199\u7684\u6027\u80FD\u662F\u5F88\u9AD8\u7684\uFF0C\u8FD9\u5C31\u662F kafka \u7684\u601D\u8DEF\u3002</li><li>\u5176\u6B21\u4F60\u8003\u8651\u4E00\u4E0B <strong>\u4F60\u7684 mq \u7684\u53EF\u7528\u6027</strong> \u554A\uFF1F\u8FD9\u4E2A\u4E8B\u513F\uFF0C\u5177\u4F53\u53C2\u8003\u4E4B\u524D\u53EF\u7528\u6027\u90A3\u4E2A\u73AF\u8282\u8BB2\u89E3\u7684 kafka \u7684\u9AD8\u53EF\u7528\u4FDD\u969C\u673A\u5236\u3002\u591A\u526F\u672C -&gt; leader &amp; follower -&gt; broker \u6302\u4E86\u91CD\u65B0\u9009\u4E3E leader \u5373\u53EF\u5BF9\u5916\u670D\u52A1\u3002</li><li><strong>\u80FD\u4E0D\u80FD\u652F\u6301\u6570\u636E 0 \u4E22\u5931</strong>\u554A\uFF1F\u53EF\u4EE5\u7684\uFF0C\u53C2\u8003\u6211\u4EEC\u4E4B\u524D\u8BF4\u7684\u90A3\u4E2A kafka \u6570\u636E\u96F6\u4E22\u5931\u65B9\u6848\u3002</li></ul><p>mq \u80AF\u5B9A\u662F\u5F88\u590D\u6742\u7684\uFF0C\u9762\u8BD5\u5B98\u95EE\u4F60\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5176\u5B9E\u662F\u4E2A\u5F00\u653E\u9898\uFF0C\u4ED6\u5C31\u662F\u770B\u770B\u4F60\u6709\u6CA1\u6709\u4ECE\u67B6\u6784\u89D2\u5EA6\u6574\u4F53\u6784\u601D\u548C\u8BBE\u8BA1\u7684\u601D\u7EF4\u4EE5\u53CA\u80FD\u529B\u3002\u786E\u5B9E\u8FD9\u4E2A\u95EE\u9898\u53EF\u4EE5\u5237\u6389\u4E00\u5927\u6279\u4EBA\uFF0C\u56E0\u4E3A\u5927\u90E8\u5206\u4EBA\u5E73\u65F6\u4E0D\u601D\u8003\u8FD9\u4E9B\u4E1C\u897F\u3002</p>',19);function k(f,M){const r=i("ExternalLinkIcon");return p(),s("div",null,[h,g,a("blockquote",null,[a("p",null,[a("a",d,[l,e(r)])])]),u,a("p",null,[a("a",m,[_,e(r)]),b]),q])}var R=o(c,[["render",k],["__file","mianshi.html.vue"]]);export{R as default};
